import './Home.css';
import React, { useState } from 'react';

function Home() {

    const [request, setRequest] = useState("[Enter song and artist]");
    const [requests, setRequests] = useState([]);

    function addRequest() {
        const newRequest = requests.concat({ request: request, id: request.length+1});

        setRequests(newRequest);
    }

    return (
        <div className="Home">
            <div className="Header">
                Phi Tau Music Request Site
            </div>
            <div className="Request">
                <input className="SongName" value={request} type="text" onChange={ e => setRequest(e.target.value) }/>
            </div>
            <button className="RequestButtonB" onClick={addRequest}>
                <div className="RequestButton">
                    Request
                </div>
            </button>
            <div className="ListOfRequests">
                {requests.map((req) => (
                    <div className='RequestsPreview' key={req.id}> {req.request} </div>
                ))}
            </div>
        </div>
    );
}

export default Home;
