import './Home.css';
import React, { useEffect, useState } from 'react';
import RequestsList from './RequestsList';
import Axios from 'axios';

function Home() {
    const [songRequest, setSongRequest] = useState("Enter Song");
    const [artistRequest, setArtistRequest] = useState("Enter Artist");
    const [requests, setRequests] = useState([]);
    const [errorMessage, setErrorMessage] = useState("");

    function refreshPage() {
        window.location.reload(false);
    }

    const submitRequest = () => {
        if (songRequest==="Enter Song" || artistRequest==="Enter Artist") {
            setSongRequest("");
            setArtistRequest("");
            setErrorMessage("Cannot leave input blank");
        } else if (songRequest==="") {
            setErrorMessage("Cannot leave input blank");
        } else {
            window.location.href='http://localhost:3000/submitted';
            Axios.post("http://localhost:3001/songInsert", {
            song: songRequest,
            artist: artistRequest})
            .then(response => {
                this.props.handleSuccessfullFormSubmission(response.data);
                window.location.reload();
            }).catch(error => {
                console.log("handlesubmit error for blog ", error)
            });
            setErrorMessage("");
            setSongRequest("");
            setArtistRequest("");
        }
    }

    function addRequest() {
        if (songRequest==="Enter Song" || artistRequest==="Enter Artist") {
            setSongRequest("");
            setArtistRequest("");
            setErrorMessage("Cannot leave input blank");
        } else if (songRequest==="") {
            setErrorMessage("Cannot leave input blank");
        } else {
            setErrorMessage("");
            const newRequest = requests.concat({ songRequest: songRequest + " by ", artistRequest: artistRequest, id: requests.length+1});
            setSongRequest("");
            setArtistRequest("");
            setRequests(newRequest);
        }
    }

    return (
        <div className="Home">
            <div className="Header">
                Phi Tau Music Request Site
            </div>
            <div className="SongRequest">
                <input className="Request" value={songRequest} type="text" onChange={ e => setSongRequest(e.target.value) }/>
            </div>
            <div className="ArtistRequest">
                <input className="Request" value={artistRequest} type="text" onChange={ e => setArtistRequest(e.target.value) }/>
            </div>
            <div className="ErrorMessage">
                <div className="ErrorMessageText">
                    {errorMessage}
                </div>
            </div>
            <button className="RequestButton" onClick={submitRequest}>
                <div className="RequestButtonText">
                    Request
                </div>
            </button>
            <div className="QueueHeading">
                <div className="QueueHeadingText">
                    Queue
                </div>
            </div>
            <div>
                < RequestsList />
            </div>
        </div>
    );
}

export default Home;
