import './Home.css';
import React, { useState } from 'react';

function Home() {
    const [songRequest, setSongRequest] = useState("Enter Song");
    const [artistRequest, setArtistRequest] = useState("Enter Artist");
    const [requests, setRequests] = useState([]);
    const [errorMessage, setErrorMessage] = useState("");

    function addRequest() {
        if (songRequest==="") {
            setErrorMessage("Cannot leave input blank");
        } else {
            setErrorMessage("");
            const newRequest = requests.concat({ songRequest: songRequest + " by ", artistRequest: artistRequest, id: requests.length+1});
            setSongRequest("");
            setArtistRequest("");
            setRequests(newRequest);
        }

        if (songRequest==="Enter Song" || artistRequest==="Enter Artist") {
            setSongRequest("");
            setArtistRequest("");
        }
    }

    return (
        <div className="Home">
            <div className="Header">
                Phi Tau Music Request Site
            </div>
            <div className="SongRequest">
                <input className="Request" value={songRequest} type="text" onChange={ e => setSongRequest(e.target.value) }/>
            </div>
            <div className="ArtistRequest">
                <input className="Request" value={artistRequest} type="text" onChange={ e => setArtistRequest(e.target.value) }/>
            </div>
            <div className="ErrorMessage">
                <div className="ErrorMessageText">
                    {errorMessage}
                </div>
            </div>
            <button className="RequestButton" onClick={addRequest}>
                <div className="RequestButtonText">
                    Request
                </div>
            </button>
            <div className="QueueHeading">
                <div className="QueueHeadingText">
                    Queue
                </div>
            </div>
            <div className="ListOfRequests">
                {requests.map((req) => (
                    <div className='RequestsPreview' key={req.id}> {req.songRequest} {req.artistRequest}</div>
                ))}
            </div>
        </div>
    );
}

export default Home;
