import React, { useState } from 'react';
import Home from './Home';
import Axios from 'axios';

const RequestsList = () => {

    const [requests, setRequests] = useState([]);

    const getSongs = () => {
        Axios.get('http://localhost:3001/songGet').then((response)=> {
            setRequests(response.data);
            console.log(requests);
        });
    };
    useEffect(()=> {
        getSongs();
    }, []);

    // const [songRequest, setSongRequest] = useState("Enter Song");
    // const [artistRequest, setArtistRequest] = useState("Enter Artist");
    // const [requests, setRequests] = useState([]);
    // const [errorMessage, setErrorMessage] = useState("");

    return (
        <div className="ListOfRequests">
                {requests.map((item) =>
                    <div key={item.SONG}> {item.SONG} {item.ARTIST} </div>
                )}
        </div>
    )
}

export default RequestsList;
